import { Link, NavLink } from "react-router-dom";
import styled from "styled-components";
import { motion } from "framer-motion";
import { useState } from "react";

const Bar = styled.header`
  position: sticky;
  top: 0;
  z-index: 20;
  backdrop-filter: blur(8px);
  background: rgba(11,18,16,.75);
  border-bottom: 1px solid rgba(212,175,55,.15);
`;

const Wrap = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 12px;
  padding: 14px 24px;
  max-width: 1200px;
  margin: 0 auto;
`;

const Brand = styled(Link)`
  font-weight: 800;
  letter-spacing: .3px;
  color: var(--gold-strong);
  font-size: 1.1rem;
`;

const Menu = styled.nav<{open?: boolean}>`
  display: flex;
  gap: 14px;
  align-items: center;

  a {
    display: inline-block;
    padding: 8px 12px;
    border-radius: 10px;
    color: var(--text);
    opacity: .9;
  }
  a.active {
    background: var(--muted);
    border: 1px solid rgba(212,175,55,.25);
    color: var(--gold-strong);
  }

  @media (max-width: 860px) {
    position: fixed;
    inset: 56px 10px auto auto;
    display: ${props => props.open ? 'flex' : 'none'};
    flex-direction: column;
    background: rgba(11,18,16,.98);
    border: 1px solid rgba(212,175,55,.2);
    padding: 12px;
    border-radius: 16px;
  }
`;

const Burger = styled.button`
  display: none;
  @media (max-width: 860px) { display: inline-flex; }
  padding: 8px 10px;
  border-radius: 10px;
  border: 1px solid rgba(212,175,55,.25);
  background: var(--muted);
  color: var(--text);
`;

export const Navbar = () => {
  const [open, setOpen] = useState(false);
  return (
    <Bar as={motion.header} initial={{y:-50, opacity:0}} animate={{y:0, opacity:1}} transition={{duration:.6}}>
      <Wrap>
        <Brand to='/'>Intel. Financeira</Brand>
        <Burger onClick={()=>setOpen(v=>!v)}>☰</Burger>
        <Menu open={open} onClick={()=>setOpen(false)}>
          <NavLink to='/metodo'>Método</NavLink>
          <NavLink to='/depoimentos'>Quem aplicou</NavLink>
          <NavLink to='/especialista'>Especialista</NavLink>
          <NavLink to='/comparativo'>Com x Sem</NavLink>
          <NavLink to='/bonus'>Bônus</NavLink>
          <NavLink to='/faq'>FAQ</NavLink>
          <NavLink to='/contato'>Contato</NavLink>
        </Menu>
      </Wrap>
    </Bar>
  );
};
