  CarouselWrapper,
  TestimonialCard,
  TestimonialImage,
  Avatar,
  ClientName,
  ClientTitle,
  Quote,
  Rating,
  Star,
  ImagePlaceholder,
  VerifiedBadge,
  ResultStats,
  StatNumber,
  StatLabel,
} from "./styles";

function Testimonials() {
  const [isPaused, setIsPaused] = useState(false);

  const testimonials = [
