import styled, { keyframes, css } from "styled-components";

/* --- animações --- */
const float = keyframes`
  0% { transform: translateY(0) translateX(0); }
  50% { transform: translateY(10px) translateX(6px); }
  100% { transform: translateY(0) translateX(0); }
`;

const glowRun = keyframes`
  0% { background-position: 0% 50%; }
  100% { background-position: 200% 50%; }
`;

/* --- layout --- */
export const Wrap = styled.section`
  position: relative;
  margin: 56px 0;
  padding: 8px 0 0;
`;

export const Badge = styled.h2`
  width: fit-content;
  margin: 0 auto 18px;
  padding: 6px 12px;
  font-size: 0.95rem;
  letter-spacing: .4px;
  color: var(--text);
  border-radius: 12px;
  border: 1px solid rgba(212,175,55,.25);
  background: rgba(0,0,0,.35);
  text-align: center;
`;

export const Aura = styled.div`
  position: absolute;
  inset: -10% -5% auto -5%;
  height: 190px;
  pointer-events: none;
  background:
    radial-gradient(600px 220px at 20% 40%, rgba(89,255,168,.10), transparent 60%),
    radial-gradient(600px 220px at 80% 60%, rgba(212,175,55,.10), transparent 60%);
  filter: saturate(120%);
  &::after{
    content:"";
    position:absolute;
    left:10%;
    top:10%;
    width:220px; height:220px;
    border-radius:50%;
    background: radial-gradient(circle, rgba(89,255,168,.12), transparent 60%);
    filter: blur(12px);
    animation: ${float} 9s ease-in-out infinite;
  }
`;

export const Grid = styled.div`
  display: grid;
  gap: 14px;
`;

/* --- cartão/accordion --- */
export const Card = styled.div<{ "data-open"?: boolean }>`
  position: relative;
  border-radius: 16px;
  border: 1px solid rgba(212,175,55,.18);
  background: var(--card);
  overflow: hidden;

  /* borda animada quando aberto */
  &::before{
    content:"";
    position:absolute;
    left:0; top:0; bottom:0; width: 3px;
    background: linear-gradient(90deg, var(--gold), var(--gold-strong), var(--gold));
    background-size: 200% 100%;
    opacity: ${({["data-open"]:open}) => (open ? 1 : .25)};
    animation: ${({["data-open"]:open}) => open ? css`${glowRun} 1.8s linear infinite` : "none"};
  }
`;

export const Header = styled.button`
  all: unset;
  display: grid;
  grid-template-columns: 1fr auto;
  align-items: center;
  gap: 12px;
  width: 100%;
  padding: 16px 18px;
  cursor: pointer;

  &:hover { background: rgba(255,255,255,.02); }
  &:focus-visible {
    outline: 2px solid rgba(242,201,76,.35);
    outline-offset: 2px;
    border-radius: 16px;
  }
`;

export const Title = styled.strong`
  color: var(--text);
  font-weight: 800;
  letter-spacing: .2px;
`;

export const Chevron = styled.span<{ "data-open"?: boolean }>`
  width: 22px; height: 22px;
  border: 2px solid rgba(212,175,55,.5);
  border-left: 0; border-bottom: 0;
  transform: rotate(45deg);
  border-radius: 4px;
  transition: transform .25s ease, border-color .25s ease, opacity .25s ease;
  opacity: .9;

  ${({["data-open"]:open}) =>
    open && css`
      transform: rotate(135deg);
      border-color: var(--gold-strong);
    `}
`;

export const Panel = styled.div<{ "data-open"?: boolean }>`
  display: grid;
  grid-template-rows: ${({["data-open"]:open}) => (open ? "1fr" : "0fr")};
  transition: grid-template-rows .28s ease;
`;

export const Answer = styled.div`
  overflow: hidden;
  padding: 0 18px 16px 18px;
  color: var(--subtext);
  line-height: 1.5;
  > p { margin: 0; }
`;
