import React, { useState, useEffect } from "react";
import { CheckCircle } from "lucide-react";
import {
  TestimonialsContainer,
  Container,
  TitleGroup,
  Title,
  Grid,
  TestimonialCard,
  TestimonialImage,
  Avatar,
  ClientName,
  ClientTitle,
  Quote,
  Rating,
  Star,
  ImagePlaceholder,
  VerifiedBadge,
  ResultStats,
  StatNumber,
  StatLabel,
} from "./styles";

function Testimonials() {
  const [visibleElements, setVisibleElements] = useState(new Set());

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setVisibleElements((prev) => new Set([...prev, entry.target.id]));
          }
        });
      },
      { threshold: 0.2 }
    );

    const elements = document.querySelectorAll("[data-animate]");
    elements.forEach((el, index) => {
      el.id = `testimonial-${index}`;
      observer.observe(el);
    });

    return () => observer.disconnect();
  }, []);

  const testimonials = [
    {
      name: "Dr. Carlos Mendes",
      title: "CirurgiÃ£o Cardiovascular",
      avatar: "CM",
      quote:
        "Multiplicar meu patrimÃ´nio com estratÃ©gia foi fundamental. Em 18 meses, consegui diversificar completamente meus investimentos seguindo o mÃ©todo estoico. A tranquilidade que tenho hoje para focar na medicina Ã© impagÃ¡vel.",
      rating: 5,
      result: { number: "+127%", label: "de rentabilidade" },
      hasImage: false,
    },
    {
      name: "Ana Paula Silva",
      title: "Executiva de Marketing",
      avatar: "AS",
      quote:
        "Por anos deixei meu dinheiro parado na poupanÃ§a por medo. O Estoicismo Financeiro me ensinou a ter clareza nas decisÃµes. Hoje tenho uma carteira sÃ³lida e durmo tranquila sabendo que meu futuro estÃ¡ protegido.",
      rating: 5,
      result: { number: "R$ 850k", label: "patrimÃ´nio atual" },
      hasImage: false,
    },
    {
      name: "Roberto Almeida",
      title: "Advogado Empresarial",
      avatar: "RA",
      quote:
        "Quem jÃ¡ aplica o mÃ©todo sabe: nÃ£o Ã© sobre timing do mercado, Ã© sobre estratÃ©gia consistente. Consegui IPCA+8% de forma sustentÃ¡vel e agora posso focar no que realmente importa - minha famÃ­lia e carreira.",
      rating: 5,
      result: { number: "IPCA+8%", label: "mÃ©dia anual" },
      hasImage: false,
    },
    {
      name: "Mariana Costa",
      title: "EmpresÃ¡ria do Setor Tech",
      avatar: "MC",
      quote:
        "O mÃ©todo mudou completamente minha relaÃ§Ã£o com o dinheiro. Antes era pura ansiedade, hoje Ã© estratÃ©gia pura. Diversifiquei internacionalmente e tenho uma base sÃ³lida para crescer sem medo das oscilaÃ§Ãµes do mercado.",
      rating: 5,
      result: { number: "+89%", label: "em 24 meses" },
      hasImage: false,
    },
  ];

  const renderStars = (rating: any) => {
    return Array.from({ length: 5 }, (_, index) => (
      <Star key={index} delay={index * 0.1}>
        {index < rating ? "â˜…" : "â˜†"}
      </Star>
    ));
  };

  return (
    <TestimonialsContainer>
      <Container>
        <TitleGroup>
          <Title>Mensagens reais de quem aplicou o mÃ©todo</Title>
        </TitleGroup>

        <Grid>
          {testimonials.map((testimonial, index) => (
            <TestimonialCard
              key={index}
              data-animate
              className={
                visibleElements.has(`testimonial-${index}`) ? "visible" : ""
              }
            >
              <VerifiedBadge>
                <CheckCircle size={16} />
              </VerifiedBadge>

              {testimonial.hasImage ? (
                <TestimonialImage>
                  <img
                    src={`/assets/ref_${index + 1}.png`}
                    alt={`Depoimento ${testimonial.name}`}
                  />
                </TestimonialImage>
              ) : (
                <ImagePlaceholder>
                  <div style={{ fontSize: "2rem", marginBottom: "0.5rem" }}>
                    ðŸ“±
                  </div>
                  <div>Mensagem Real</div>
                  <div style={{ fontSize: "0.9rem", opacity: 0.8 }}>
                    Depoimento Verificado
                  </div>
                </ImagePlaceholder>
              )}

              <div
                style={{
                  display: "flex",
                  alignItems: "center",
                  gap: "1rem",
                  marginBottom: "1rem",
                }}
              >
                <Avatar>{testimonial.avatar}</Avatar>
                <div>
                  <ClientName>{testimonial.name}</ClientName>
                  <ClientTitle>{testimonial.title}</ClientTitle>
                </div>
              </div>

              <Quote>{testimonial.quote}</Quote>

              <Rating>{renderStars(testimonial.rating)}</Rating>

              <ResultStats>
                <StatNumber>{testimonial.result.number}</StatNumber>
                <StatLabel>{testimonial.result.label}</StatLabel>
              </ResultStats>
            </TestimonialCard>
          ))}
        </Grid>

        {/* Cards adicionais simulando screenshots reais */}
        <Grid style={{ marginTop: "3rem" }}>
          {[1, 2, 3, 4].map((i) => (
            <TestimonialCard
              key={`screenshot-${i}`}
              data-animate
              className={
                visibleElements.has(
                  `testimonial-${testimonials.length + i - 1}`
                )
                  ? "visible"
                  : ""
              }
            >
              <VerifiedBadge>
                <CheckCircle size={16} />
              </VerifiedBadge>

              <TestimonialImage>
                <ImagePlaceholder>
                  <div style={{ fontSize: "2rem", marginBottom: "0.5rem" }}>
                    ðŸ“Š
                  </div>
                  <div>Screenshot Real</div>
                  <div style={{ fontSize: "0.8rem", opacity: 0.7 }}>
                    Resultados de Cliente
                  </div>
                </ImagePlaceholder>
              </TestimonialImage>

              <div style={{ textAlign: "center" }}>
                <ClientName>Resultado Verificado #{i}</ClientName>
                <ClientTitle>Cliente do MÃ©todo</ClientTitle>
                <Quote
                  style={{
                    paddingLeft: 0,
                    textAlign: "center",
                    fontSize: "0.9rem",
                  }}
                >
                  "Aplicando exatamente o que o Gabriel ensina no mÃ©todo.
                  Resultados reais de quem seguiu Ã  risca."
                </Quote>

                <ResultStats>
                  <StatNumber>
                    {i === 1
                      ? "+156%"
                      : i === 2
                        ? "IPCA+12%"
                        : i === 3
                          ? "R$ 1.2M"
                          : "+203%"}
                  </StatNumber>
                  <StatLabel>
                    {i === 1
                      ? "em 2 anos"
                      : i === 2
                        ? "mÃ©dia anual"
                        : i === 3
                          ? "patrimÃ´nio"
                          : "rentabilidade"}
                  </StatLabel>
                </ResultStats>
              </div>
            </TestimonialCard>
          ))}
        </Grid>
      </Container>
    </TestimonialsContainer>
  );
}

export default Testimonials;
