import styled from "styled-components";
import { FiCheckCircle, FiXCircle } from "react-icons/fi";

const Wrap = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 24px;
  @media (max-width: 850px) {
    grid-template-columns: 1fr;
  }
`;

const Col = styled.div`
  background: var(--card);
  border: 1px solid rgba(212,175,55,.18);
  border-radius: 18px;
  padding: 24px;
`;

const Title = styled.h3`
  color: var(--gold-strong);
  margin: 0 0 12px;
`;

const Item = styled.li`
  svg { flex: 0 0 auto; }
  &.neg svg { color: #ff6b6b; }
  &.pos svg { color: #59ffa8; }

  list-style: none;
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 8px 0;
  color: var(--subtext);
`;

export default function ComparisonGrid() {
  const negatives = Array.from({length: 10}).map((_,i)=>`Texto ilustrativo`);
  const positives = Array.from({length: 10}).map((_,i)=>`Texto ilustrativo`);
  return (
    <Wrap>
      <Col>
        <Title>Sem método</Title>
        <ul>
          {negatives.map((t,i)=>(
            <Item key={i} className="neg"><FiXCircle />{t}</Item>
          ))}
        </ul>
      </Col>
      <Col>
        <Title>Com o método</Title>
        <ul>
          {positives.map((t,i)=>(
            <Item key={i} className="pos"><FiCheckCircle />{t}</Item>
          ))}
        </ul>
      </Col>
    </Wrap>
  );
}
