import ContactForm from "../../components/contact/ContactForm";
import { useState } from "react";
import { Section, Title, TitleGroup } from "./styles";

export default function Contato() {
  const [msg, setMsg] = useState<string | undefined>();
  const [err, setErr] = useState<string | undefined>();

  return (
    <Section>
      <div className="container">
        <TitleGroup>
          <Title>Fale com a gente</Title>
        </TitleGroup>

        <p style={{ opacity: 0.8, marginBottom: 15 }}>
          Envie uma mensagem — retornamos em até 1 dia útil.
        </p>
        <ContactForm success={(m) => setMsg(m)} error={(e) => setErr(e)} />
        {msg && <p style={{ color: "#59ffa8", marginTop: 10 }}>{msg}</p>}
        {err && <p style={{ color: "#ff6b6b", marginTop: 10 }}>{err}</p>}
      </div>
    </Section>
  );
}
