import styled from "styled-components";

const Banner = styled.div`
  border-radius: 18px;
  padding: clamp(18px, 3vw, 28px);
  border: 1px solid rgba(212,175,55,.25);
  background: linear-gradient(180deg, rgba(30,50,42,.8), rgba(20,32,27,.9));
  text-align: center;
  color: var(--subtext);
  font-size: clamp(1rem, 2vw, 1.2rem);
`;

const Heading = styled.h2`
  color: var(--gold-strong);
  text-align: center;
  margin-bottom: 18px;
  font-size: clamp(1.6rem, 4vw, 2.2rem);
`;

export default function BonusBanner() {
  return (
    <div className="container">
      <Heading>Bônus Exclusivo</Heading>
      <Banner>
        Receba um portfólio modelo com IPCA+6, crédito privado, REITs e dólar – para proteger e crescer seu patrimônio.
      </Banner>
    </div>
  );
}
