import {
  Container,
  TitleGroup,
  MainTitle,
  Wrap,
  Col,
  Title,
  Item,
  ItemsList,
} from "./styles";

function CrossIcon() {
  return (
    <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
      <path
        d="M18 6L6 18M6 6l12 12"
        stroke="currentColor"
        strokeWidth="2"
        strokeLinecap="round"
      />
    </svg>
  );
}

function CheckIcon() {
  return (
    <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
      <path
        d="M20 6L9 17l-5-5"
        stroke="currentColor"
        strokeWidth="2"
        strokeLinecap="round"
        strokeLinejoin="round"
      />
    </svg>
  );
}

export default function ComparisonGrid() {
  const negatives = [
    "Se assusta com notícias",
    "Não tem clareza para investir",
    "Culpa o governo pela baixa rentabilidade",
    "Vende tudo com medo de crise",
    "Não compreende no que investe",
    "Se traumatiza com o mercado",
    "Tenta ganhar no curto prazo",
  ];

  const positives = [
    "Não se abala por notícias",
    "Sabe exatamente como e onde investir",
    "Sabe que é único responsável pela carteira",
    "Entende as crises e as aproveita",
    "Compreende no que investe e ignora o que não importa",
    "Aprende com os erros e volatilidade do mercado",
    "Entende que o jogo é de PAZ no longo prazo",
  ];

  return (
    <div className="container">
      <Container>
        <TitleGroup>
          <MainTitle>Com ou sem o método?</MainTitle>
        </TitleGroup>

        <Wrap>
          <Col>
            <Title>Sem método</Title>
            <ItemsList>
              {negatives.map((text, i) => (
                <Item key={i} className="neg">
                  <CrossIcon />
                  {text}
                </Item>
              ))}
            </ItemsList>
          </Col>

          <Col>
            <Title>Com o método</Title>
            <ItemsList>
              {positives.map((text, i) => (
                <Item key={i} className="pos">
                  <CheckIcon />
                  {text}
                </Item>
              ))}
            </ItemsList>
          </Col>
        </Wrap>
      </Container>
    </div>
  );
}
