import React, { useState, useEffect } from "react";
import {
  User,
  TrendingUp,
  Target,
  Award,
  BookOpen,
  Shield,
} from "lucide-react";
import {
  Container,
  Section,
  TitleGroup,
  Title,
  HeroSection,
  ContentCard,
  ImagePlaceholder,
  IntroText,
  Description,
  InfoSection,
  InfoCard,
  InfoTitle,
  HighlightText,
  AnimatedIcon,
} from "./styles";

// hook reutiliz√°vel
function useInViewOnce(options: IntersectionObserverInit = { threshold: 0.2 }): [React.RefObject<HTMLDivElement>, boolean] {
  const ref = React.useRef<HTMLDivElement>(null);
  const [visible, setVisible] = React.useState(false);

  React.useEffect(() => {
    const obs = new IntersectionObserver(([e]) => {
      if (e.isIntersecting) {
        setVisible(true);
        obs.disconnect();
      }
    }, options);
    if (ref.current) obs.observe(ref.current);
    return () => obs.disconnect();
  }, [options]);

  return [ref, visible];
}

function MentorProfile() {
  const [visibleElements, setVisibleElements] = useState(new Set());
  const cardInView = useInViewOnce();
  const cardRef = cardInView[0];
  const cardVisible = cardInView[1];
  const imgInView = useInViewOnce();
  const imgRef = imgInView[0];
  const imgVisible = imgInView[1];

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setVisibleElements((prev) => new Set([...prev, entry.target.id]));
          }
        });
      },
      { threshold: 0.2 }
    );

    const elements = document.querySelectorAll("[data-animate]");
    elements.forEach((el, index) => {
      el.id = `animate-${index}`;
      observer.observe(el);
    });

    return () => observer.disconnect();
  }, []);

  const infoSections = [
    {
      icon: <User />,
      title: "Quem √© Gabriel Correia?",
      content:
        "Assessor de investimentos s√™nior na InvestSmart, escrit√≥rio credenciado √† XP, e idealizador do Estoicismo Financeiro. Com mais de 8 anos de experi√™ncia, dedico minha carreira a ajudar fam√≠lias e empres√°rios a proteger, multiplicar e organizar seu patrim√¥nio.",
    },
    {
      icon: <TrendingUp />,
      title: "Especializa√ß√£o e Experi√™ncia",
      content:
        "Lidero um escrit√≥rio de investimentos voltado para clientes de alta renda ‚Äî m√©dicos, advogados, executivos e empreendedores. Especializado em estrat√©gias com IPCA+6, cr√©dito privado e diversifica√ß√£o internacional.",
    },
    {
      icon: <Target />,
      title: "Metodologia e Abordagem",
      content:
        "Minha abordagem une vis√£o de longo prazo, disciplina e m√©todo. N√£o acredito em atalhos f√°ceis, nem em f√≥rmulas m√°gicas. O que funciona √© estrat√©gia: aloca√ß√£o inteligente, diversifica√ß√£o e vis√£o global.",
    },
    {
      icon: <Shield />,
      title: "Filosofia Estoica",
      content:
        "A filosofia estoica me acompanha como princ√≠pio de vida e ferramenta financeira. Defendo que o patrim√¥nio deve ser instrumento de liberdade, legado e realiza√ß√£o, n√£o fonte de ansiedade.",
    },
    {
      icon: <BookOpen />,
      title: "Objetivo do Curso",
      content:
        "Mostrar como aplicar os fundamentos do estoicismo na sua vida financeira, ajudando voc√™ a tomar decis√µes racionais e conquistar uma rela√ß√£o madura com o dinheiro.",
    },
    {
      icon: <Award />,
      title: "O Estoicismo Financeiro",
      content:
        "N√£o √© sobre ter mais, √© sobre ter melhor. √â sobre construir riqueza com prop√≥sito e serenidade, sem deixar que a euforia ou o medo ditem os rumos do seu futuro.",
    },
  ];

  // hook reutiliz√°vel
  function useInViewOnce(options = { threshold: 0.2 }) {
    const ref = React.useRef(null);
    const [visible, setVisible] = React.useState(false);

    React.useEffect(() => {
      const obs = new IntersectionObserver(([e]) => {
        if (e.isIntersecting) {
          setVisible(true);
          obs.disconnect();
        }
      }, options);
      if (ref.current) obs.observe(ref.current);
      return () => obs.disconnect();
    }, [options]);

    return [ref, visible];
  }

  return (
    <div className="container">
      <Container>
        <Section>
          <div style={{ width: "100%" }}>
            <TitleGroup>
              <Title>Quem √© o seu mentor em finan√ßas?</Title>
            </TitleGroup>

            <HeroSection>
              <ContentCard
                ref={cardRef}
                className={cardVisible ? "visible" : ""}
              >
                <IntroText>Gabriel Correia</IntroText>
                <Description>
                  <HighlightText>
                    Assessor de investimentos s√™nior
                  </HighlightText>{" "}
                  com anos de atua√ß√£o pr√°tica no mercado. Especializado em
                  estrat√©gias com IPCA+6, cr√©dito privado e diversifica√ß√£o
                  internacional.
                </Description>
                <Description>
                  <strong>Sua miss√£o √© simples:</strong> ajudar quem j√° tem
                  dinheiro a tomar decis√µes com clareza e m√©todo.
                </Description>
              </ContentCard>

              <ImagePlaceholder
                ref={imgRef}
                className={imgVisible ? "visible" : ""}
              >
                <AnimatedIcon>üì∏</AnimatedIcon>
              </ImagePlaceholder>
            </HeroSection>
          </div>
        </Section>

        <div style={{ paddingTop: "4rem" }} />

        {infoSections.map((section, i) => {
          const [ref, vis] = useInViewOnce();
          return (
            <Section key={i}>
              <InfoSection>
                <InfoCard
                  ref={ref}
                  direction={i % 2 === 0 ? "left" : "right"}
                  className={vis ? "visible" : ""}
                >
                  <InfoTitle>
                    <AnimatedIcon>{section.icon}</AnimatedIcon>
                    {section.title}
                  </InfoTitle>
                  <Description>{section.content}</Description>
                </InfoCard>
              </InfoSection>
            </Section>
          );
        })}
      </Container>
    </div>
  );
}

export default MentorProfile;
