import { Section } from "../components/Section";
import { Title } from "../components/AnimatedHeading";
import ContactForm from "../components/ContactForm";
import { useState } from "react";

export default function Contato() {
  const [msg, setMsg] = useState<string|undefined>();
  const [err, setErr] = useState<string|undefined>();

  return (
    <Section>
      <div className="container">
        <Title as='h2'>Fale com a gente</Title>
        <p style={{opacity:.8, marginBottom:12}}>Envie uma mensagem — retornamos em até 1 dia útil.</p>
        <ContactForm success={(m)=>setMsg(m)} error={(e)=>setErr(e)} />
        {msg && <p style={{color:'#59ffa8', marginTop:10}}>{msg}</p>}
        {err && <p style={{color:'#ff6b6b', marginTop:10}}>{err}</p>}
      </div>
    </Section>
  );
}
