import { useEffect, useMemo, useState } from "react";
import emailjs from "emailjs-com";
import {
  Button,
  Input,
  Row,
  Row3,
  Select,
  Textarea,
  Wrap,
  CheckWrap,
  CheckInput,
  CheckLabel,
  Container,
  ButtonBase,
} from "./styles";
import GlobalCTA from "../Button";
import { styled } from 'styled-components';

type Props = {
  success?: (msg: string) => void;
  error?: (msg: string) => void;
};

const PATRIMONIO_OPCOES = [
  "Até R$ 10 mil",
  "R$ 10 mil – R$ 50 mil",
  "R$ 50 mil – R$ 200 mil",
  "R$ 200 mil – R$ 1 milhão",
  "Acima de R$ 1 milhão",
];

export default function ContactForm({ success, error }: Props) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [patrimonio, setPatrimonio] = useState(PATRIMONIO_OPCOES[0]);
  const [conhecia, setConhecia] = useState(false);
  const [message, setMessage] = useState("Quero começar com inteligência.");
  const [sending, setSending] = useState(false);

  const formSubmitEmail = useMemo(
    () => process.env.REACT_APP_FORM_SUBMIT_EMAIL || "",
    []
  );

  useEffect(() => {
    const key = process.env.REACT_APP_EMAILJS_PUBLIC_KEY;
    if (key) emailjs.init(key);
  }, []);

  function normalizePhone(raw: string) {
    return raw.replace(/\D/g, "");
  }

  async function onSubmit(e: React.FormEvent) {
    e.preventDefault();
    setSending(true);
    try {
      const payload = {
        name,
        email,
        phone: normalizePhone(phone),
        patrimonio,
        conhecia: conhecia ? "Sim" : "Não",
        message,
      };

      if (formSubmitEmail) {
        const data = new FormData();
        Object.entries(payload).forEach(([k, v]) => data.append(k, String(v)));
        data.append("_subject", "Novo contato do site");
        await fetch(
          `https://formsubmit.co/ajax/${encodeURIComponent(formSubmitEmail)}`,
          { method: "POST", body: data }
        );
        success?.("Enviado com sucesso. Verifique seu e-mail.");
      } else if (
        process.env.REACT_APP_EMAILJS_SERVICE_ID &&
        process.env.REACT_APP_EMAILJS_TEMPLATE_ID
      ) {
        await emailjs.send(
          process.env.REACT_APP_EMAILJS_SERVICE_ID!,
          process.env.REACT_APP_EMAILJS_TEMPLATE_ID!,
          payload
        );
        success?.("Enviado com sucesso via EmailJS!");
      } else {
        console.log("Contato (simulado):", payload);
        success?.("Simulado (preencha .env para envio real).");
      }

      // reset
      setName("");
      setEmail("");
      setPhone("");
      setPatrimonio(PATRIMONIO_OPCOES[0]);
      setConhecia(false);
      setMessage("");
    } catch {
      error?.("Falha ao enviar, tente novamente.");
    } finally {
      setSending(false);
    }
  }

  return (
    <Wrap onSubmit={onSubmit}>
      <Container>
        <Row>
          <Input
            required
            placeholder="Seu nome"
            value={name}
            onChange={(e) => setName(e.target.value)}
            disabled={sending}
            aria-label="Nome"
          />
          <Input
            required
            type="email"
            placeholder="Seu e-mail"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            disabled={sending}
            aria-label="E-mail"
          />
        </Row>

        <Row3>
          <Input
            required
            type="tel"
            inputMode="tel"
            placeholder="Seu telefone (WhatsApp)"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            disabled={sending}
            aria-label="Telefone"
          />

          <Select
            value={patrimonio}
            onChange={(e) => setPatrimonio(e.target.value)}
            disabled={sending}
            aria-label="Patrimônio investido"
          >
            {PATRIMONIO_OPCOES.map((opt) => (
              <option key={opt} value={opt}>
                {opt}
              </option>
            ))}
          </Select>

          <CheckWrap>
            <CheckInput
              id="conhecia"
              type="checkbox"
              checked={conhecia}
              onChange={(e) => setConhecia(e.target.checked)}
              disabled={sending}
            />
            <CheckLabel>Já conhecia a gente?</CheckLabel>
          </CheckWrap>
        </Row3>

        <Textarea
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Conte um pouco do que você busca…"
          disabled={sending}
          aria-label="Mensagem"
        />
        {/* 
        <ButtonBase>
          <Button disabled={sending}>
            {sending ? "Enviando..." : "Enviar"}
          </Button>
        </ButtonBase> */}

        <GlobalCTA
          href="/contato"
          disabled={sending}
          type="submit"
          size="medium"
          style={{ with: "100%" }}
        >
          {" "}
          {sending ? "Enviando..." : "Enviar"}
        </GlobalCTA>
      </Container>
    </Wrap>
  );
}
