import styled from "styled-components";
import { useEffect, useMemo, useState } from "react";
import emailjs from "emailjs-com";

const Wrap = styled.form`
  display: grid;
  gap: 14px;
  background: var(--card);
  border: 1px solid rgba(212,175,55,.18);
  border-radius: 18px;
  padding: 18px;
`;

const Row = styled.div`
  display: grid;
  gap: 12px;
  grid-template-columns: 1fr 1fr;
  @media (max-width: 780px) { grid-template-columns: 1fr; }
`;

const Input = styled.input`
  background: #0f1a16;
  border: 1px solid rgba(212,175,55,.18);
  border-radius: 12px;
  padding: 12px 14px;
  color: var(--text);
  outline: none;
`;

const Textarea = styled.textarea`
  background: #0f1a16;
  border: 1px solid rgba(212,175,55,.18);
  border-radius: 12px;
  padding: 12px 14px;
  color: var(--text);
  min-height: 120px;
  outline: none;
`;

const Button = styled.button`
  justify-self: start;
  padding: 12px 16px;
  border-radius: 12px;
  background: linear-gradient(135deg, #d4af37, #f2c94c);
  color: #121212;
  font-weight: 800;
  border: 0;
  cursor: pointer;
  box-shadow: 0 10px 30px rgba(242,201,76,.25);
`;

type Props = {
  success?: (msg: string)=>void;
  error?: (msg: string)=>void;
};

export default function ContactForm({ success, error }: Props) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("Quero começar com inteligência.");
  const [sending, setSending] = useState(false);

  const formSubmitEmail = useMemo(()=>process.env.REACT_APP_FORM_SUBMIT_EMAIL || "", []);

  useEffect(()=>{
    // init emailjs if used
    const key = process.env.REACT_APP_EMAILJS_PUBLIC_KEY;
    if (key) emailjs.init(key);
  }, []);

  async function onSubmit(e: React.FormEvent) {
    e.preventDefault();
    setSending(true);
    try {
      if (formSubmitEmail) {
        // Use formsubmit.co (no JS request, but we simulate here)
        const data = new FormData();
        data.append("name", name);
        data.append("email", email);
        data.append("message", message);
        data.append("_subject", "Novo contato do site");
        await fetch(`https://formsubmit.co/ajax/${encodeURIComponent(formSubmitEmail)}`, { method: "POST", body: data });
        success?.("Enviado com sucesso. Verifique seu e-mail.");
      } else if (process.env.REACT_APP_EMAILJS_SERVICE_ID && process.env.REACT_APP_EMAILJS_TEMPLATE_ID) {
        await emailjs.send(
          process.env.REACT_APP_EMAILJS_SERVICE_ID!,
          process.env.REACT_APP_EMAILJS_TEMPLATE_ID!,
          { name, email, message }
        );
        success?.("Enviado com sucesso via EmailJS!");
      } else {
        console.log({name, email, message});
        success?.("Simulado (preencha .env para envio real).");
      }
      setName(""); setEmail(""); setMessage("");
    } catch (err: any) {
      error?.("Falha ao enviar, tente novamente.");
    } finally {
      setSending(false);
    }
  }

  return (
    <Wrap onSubmit={onSubmit}>
      <Row>
        <Input required placeholder="Seu nome" value={name} onChange={e=>setName(e.target.value)} />
        <Input required type="email" placeholder="Seu e-mail" value={email} onChange={e=>setEmail(e.target.value)} />
      </Row>
      <Textarea value={message} onChange={e=>setMessage(e.target.value)} />
      <Button disabled={sending}>{sending ? "Enviando..." : "Enviar"}</Button>
    </Wrap>
  );
}
