import { useId, useState } from "react";
import {
  Wrap,
  Grid,
  Card,
  Header,
  Title,
  Chevron,
  Panel,
  Answer,
  Badge,
  Aura,
} from "./styles";

type Item = { q: string; a: string };

const items: Item[] = [
  {
    q: "Esse método é para mim?",
    a: "Se você já tem patrimônio, mas sente insegurança para tomar decisões, sim. O método traz clareza, processo e gestão de risco para perfis ocupados.",
  },
  {
    q: "Preciso entender de economia?",
    a: "Não. Foi criado para quem quer praticidade e resultado. Você segue passos objetivos e recebe diretrizes de acompanhamento.",
  },
  {
    q: "Qual investimento mínimo?",
    a: "Não exigimos mínimo. O método adapta alocação e rebalanceamento ao seu momento e à liquidez disponível.",
  },
  {
    q: "Quanto tempo até ver resultado?",
    a: "Depende do objetivo (renda, proteção, crescimento). Em geral, nas primeiras semanas você já percebe clareza de alocação e redução de ansiedade.",
  },
  {
    q: "Posso cancelar quando quiser?",
    a: "Sim. Sem fidelidade. Você mantém o que já foi implementado e pode retomar quando fizer sentido.",
  },
];

export default function FAQ() {
  const baseId = useId();
  const [open, setOpen] = useState<number | null>(0); // abre o primeiro por padrão

  const toggle = (idx: number) =>
    setOpen((curr) => (curr === idx ? null : idx));

  return (
    <div className="container">
      <Wrap>
        <Badge>Dúvidas Frequentes</Badge>
        <Aura aria-hidden="true" />
        <Grid>
          {items.map((it, i) => {
            const expanded = open === i;
            const panelId = `${baseId}-panel-${i}`;
            const headerId = `${baseId}-header-${i}`;
            return (
              <Card key={i} data-open={expanded}>
                <Header
                  id={headerId}
                  aria-controls={panelId}
                  aria-expanded={expanded}
                  onClick={() => toggle(i)}
                >
                  <Title>{it.q}</Title>
                  <Chevron aria-hidden="true" data-open={expanded} />
                </Header>

                <Panel id={panelId} role="region" aria-labelledby={headerId} data-open={expanded}>
                  <Answer>{it.a}</Answer>
                </Panel>
              </Card>
            );
          })}
        </Grid>
      </Wrap>
    </div>
  );
}
