  align-items: center;
  margin-top: 0.5rem;

  @media (max-width: 960px) {
    grid-template-columns: 1fr;
    gap: 16px;
    text-align: center;
  }
`;

export const Brand = styled.div`
  display: flex;
  align-items: center;
  gap: 14px;
  min-width: 0;

  @media (max-width: 960px) {
    flex-direction: column;
    justify-content: center;
