import { Title, Subtitle } from "../components/AnimatedHeading";
import VideoEmbed from "../components/VideoEmbed";
import { Section, Card } from "../components/Section";
import { FloatingCard } from "../components/FloatingCard";
import styled from "styled-components";
import { motion } from "framer-motion";
import BonusBanner from "../components/bonus/BonusBanner";
import FAQ from "../components/faq/FAQ";
import { Link } from "react-router-dom";
import ComparisonGrid from "../components/comparison/ComparisonGrid";
import MentorProfile from "./mentor/MentorProfile";
import Testimonials from "./testimonials/Testimonial";
import WhyMethodWorks from "./metodo/method";

const Grid = styled.div`
  display: grid;
  gap: 18px;
  grid-template-columns: repeat(4, 1fr);
  @media (max-width: 1000px) {
    grid-template-columns: 1fr 1fr;
  }
  @media (max-width: 620px) {
    grid-template-columns: 1fr;
  }
`;

const Bullets = styled.ul`
  display: grid;
  gap: 14px;
  max-width: 800px;
  margin: 0 auto;
  li {
    color: var(--subtext);
  }
`;

export default function Home() {
  const videoId = process.env.REACT_APP_PRIMARY_VIDEO_ID || "dQw4w9WgXcQ";
  return (
    <main>
      <Section>
        <div className="container">
          <Title
            initial={{ opacity: 0, y: 16 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.7 }}
          >
            Multiplique seu patrimônio com estratégia
          </Title>
          <Subtitle
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.4, duration: 0.7 }}
          >
            O método financeiro para quem já tem dinheiro – e quer parar de
            errar nas decisões
          </Subtitle>

          <Card style={{ marginTop: 20 }}>
            <VideoEmbed id={videoId} />
          </Card>

          <div
            style={{ display: "flex", justifyContent: "center", marginTop: 20 }}
          >
            <Link
              to="/contato"
              style={{
                padding: "14px 18px",
                borderRadius: 14,
                background: "linear-gradient(135deg,#d4af37,#f2c94c)",
                color: "#111",
                fontWeight: 800,
                textDecoration: "none",
                boxShadow: "0 10px 30px rgba(242,201,76,.25)",
              }}
            >
              Quero investir com inteligência
            </Link>
          </div>
        </div>
      </Section>

      <Section id="porque-funciona">
        <div className="container">
          <Title
            as={motion.h2}
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
          >
            Por que este método funciona?
          </Title>
          <Bullets>
            <li>📈 Alocação baseada em juros reais e ciclo econômico</li>
            <li>💰 Estratégia de liquidez e proteção de capital</li>
            <li>🧠 Tomada de decisão fria e sem ruído</li>
            <li>🌍 Diversificação global com racionalidade</li>
          </Bullets>
        </div>
      </Section>

      <Section id="porque-funciona">
        <WhyMethodWorks />
      </Section>

      {/* <Section>
        <Testimonials />
      </Section> */}

      {/* <Section>
        <MentorProfile />
      </Section> */}

      {/* <Section>
        <ComparisonGrid />
      </Section> */}

      {/* <Section>
        <BonusBanner />
      </Section> */}

      {/* <Section>
        <FAQ />
      </Section> */}
    </main>
  );
}
