  Title,
  Banner,
  BonusText,
  CallText,
  CallTitle,
  CallDescription,
  FloatingElement,
  PulsingCircle,
  GiftIcon,
  AnimatedCard,
  FeatureList,
  FeatureItem,
  GlowEffect,
  ShimmerOverlay,
} from "./styles";
import GlobalCTA from "../Button";

export default function BonusBanner() {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
