import styled from "styled-components";

const Wrap = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 24px;
  @media (max-width: 850px) {
    grid-template-columns: 1fr;
  }
`;
const Col = styled.div`
  background: var(--card);
  border: 1px solid rgba(212, 175, 55, 0.18);
  border-radius: 18px;
  padding: 24px;
`;
const Title = styled.h3`
  color: var(--gold-strong);
  margin: 0 0 12px;
`;
const Item = styled.li`
  list-style: none;
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 8px 0;
  color: var(--subtext);
  svg {
    flex: 0 0 auto;
  }
  &.neg svg {
    color: #ff6b6b;
  }
  &.pos svg {
    color: #59ffa8;
  }
`;

function CrossIcon() {
  return (
    <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
      <path
        d="M18 6L6 18M6 6l12 12"
        stroke="currentColor"
        strokeWidth="2"
        strokeLinecap="round"
      />
    </svg>
  );
}
function CheckIcon() {
  return (
    <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
      <path
        d="M20 6L9 17l-5-5"
        stroke="currentColor"
        strokeWidth="2"
        strokeLinecap="round"
        strokeLinejoin="round"
      />
    </svg>
  );
}

export default function ComparisonGrid() {
  const negatives = Array.from({ length: 10 }).map(() => "Texto ilustrativo");
  const positives = Array.from({ length: 10 }).map(() => "Texto ilustrativo");
  return (
    <Wrap>
      <Col>
        <Title>Sem método</Title>
        <ul>
          {negatives.map((t, i) => (
            <Item key={i} className="neg">
              <CrossIcon />
              {t}
            </Item>
          ))}
        </ul>
      </Col>
      <Col>
        <Title>Com o método</Title>
        <ul>
          {positives.map((t, i) => (
            <Item key={i} className="pos">
              <CheckIcon />
              {t}
            </Item>
          ))}
        </ul>
      </Col>
    </Wrap>
  );
}
