import styled from "styled-components";

const Wrap = styled.footer`
  border-top: 1px solid rgba(212,175,55,.15);
  margin-top: 60px;
  padding: 40px 24px;
  color: var(--subtext);
  background: rgba(0,0,0,.35);
`;

const Inner = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 16px;
  font-size: .95rem;
  flex-wrap: wrap;
`;

const CTA = styled.a`
  padding: 12px 16px;
  border-radius: 12px;
  background: linear-gradient(135deg, #d4af37, #f2c94c);
  color: #121212;
  font-weight: 700;
  border: 0;
  cursor: pointer;
  text-decoration: none;
  box-shadow: 0 10px 30px rgba(242,201,76,.25);
`;

export const Footer = () => {
  const year = new Date().getFullYear();
  return (
    <Wrap>
      <Inner>
        <div>© {year} Inteligência Financeira S.A. • Todos os direitos reservados.</div>
        <CTA href='/contato'>Quero começar agora</CTA>
      </Inner>
    </Wrap>
  );
};
