// BonusBanner.jsx
import React, { useState, useEffect } from "react";
import {
  Container,
  TitleGroup,
  Title,
  Banner,
  BonusText,
  CallText,
  CallTitle,
  CallDescription,
  FloatingElement,
  PulsingCircle,
  GiftIcon,
  AnimatedCard,
  FeatureList,
  FeatureItem,
  GlowEffect,
  ShimmerOverlay,
} from "./styles";
import GlobalCTA from "../Button";

export default function BonusBanner() {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setIsVisible(true);
          }
        });
      },
      { threshold: 0.3 }
    );

    const element = document.querySelector("[data-bonus-animate]");
    if (element) {
      observer.observe(element);
    }

    return () => observer.disconnect();
  }, []);

  const bonusFeatures = [
    {
      icon: "üéØ",
      text: "An√°lise personalizada do seu perfil",
    },
    {
      icon: "üìä",
      text: "Estrat√©gias espec√≠ficas para seu caso",
    },
    {
      icon: "üí°",
      text: "Dicas exclusivas de aloca√ß√£o",
    },
    {
      icon: "üõ°Ô∏è",
      text: "Plano de prote√ß√£o patrimonial",
    },
  ];

  return (
    <div className="container">
      <Container>
        <TitleGroup>
          <Title>üî• B√îNUS EXCLUSIVO</Title>
        </TitleGroup>

        <Banner data-bonus-animate className={isVisible ? "visible" : ""}>
          <GlowEffect />
          <ShimmerOverlay />

          {/* <FloatingElement delay="0s">
          <GiftIcon>üéÅ</GiftIcon>
        </FloatingElement> */}

          <BonusText>
            Receba um presente especial ao se inscrever agora:
          </BonusText>

          <AnimatedCard>
            <PulsingCircle>
              <span>1h</span>
            </PulsingCircle>

            <CallText>
              <CallTitle>Call Individual com o Correia</CallTitle>
              <CallDescription>
                Uma hora inteira de conversa direta para falar sobre seu
                patrim√¥nio, tirar d√∫vidas e receber orienta√ß√£o estrat√©gica
                exclusiva.
              </CallDescription>
            </CallText>
          </AnimatedCard>

          <FeatureList>
            {bonusFeatures.map((feature, index) => (
              <FeatureItem
                key={index}
                delay={`${0.2 + index * 0.1}s`}
                className={isVisible ? "visible" : ""}
              >
                <span className="icon">{feature.icon}</span>
                <span className="text">{feature.text}</span>
              </FeatureItem>
            ))}
          </FeatureList>

          <GlobalCTA href="/contato">Quero investir!</GlobalCTA>
          {/* 
        <FloatingElement delay="1s" direction="reverse">
          ‚≠ê
        </FloatingElement> */}

          {/* <FloatingElement delay="0.5s" size="small">
          ‚ú®
        </FloatingElement> */}
        </Banner>
      </Container>
    </div>
  );
}
