import React, { useState, useEffect } from "react";
import {
  Container,
  TitleGroup,
  Title,
  Banner,
  BonusText,
  CallText,
  CallTitle,
  CallDescription,
  FloatingElement,
  PulsingCircle,
  GiftIcon,
  AnimatedCard,
  FeatureList,
  FeatureItem,
  GlowEffect,
  ShimmerOverlay,
} from "./styles";
import GlobalCTA from "../Button";

export default function BonusBanner() {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setIsVisible(true);
          }
        });
      },
      { threshold: 0.3 }
    );

    const element = document.querySelector("[data-bonus-animate]");
    if (element) {
      observer.observe(element);
    }

    return () => observer.disconnect();
  }, []);

  const bonusFeatures = [
    {
      icon: "üéØ",
      text: "An√°lise personalizada do seu perfil",
    },
    {
      icon: "üìä",
      text: "Estrat√©gias espec√≠ficas para seu caso",
    },
    {
      icon: "üí°",
      text: "Dicas exclusivas de aloca√ß√£o",
    },
    {
      icon: "üõ°Ô∏è",
      text: "Plano de prote√ß√£o patrimonial",
    },
  ];

  return (
    <div>
      <Container>
        <TitleGroup>
          <Title>üî• B√îNUS EXCLUSIVO</Title>
        </TitleGroup>

        <Banner data-bonus-animate className={isVisible ? "visible" : ""}>
