// src/components/Footer/index.tsx
import {
  Wrap,
  Inner,
  Brand,
  Logo,
  BrandText,
  Links,
  Legal,
  CTA,
} from "./styles";
// ajuste o caminho conforme a sua estrutura
import logo from "../../images/logo.png";

export const Footer = () => {
  const year = new Date().getFullYear();

  return (
    <Wrap role="contentinfo">
      <Inner className="container" style={{ gap: '1rem' }}>
        <Brand>
          <a href="/" style={{ cursor: 'pointer' }} aria-label="Ir para a página inicial">
            <Logo src={logo} alt="Correia Investimentos" loading="lazy" />
          </a>
          <BrandText>
            <strong>Correia Investimentos</strong>
            <span>Inteligência financeira para o seu patrimônio</span>
          </BrandText>
        </Brand>

        {/* <Links aria-label="Links do site">
          <a href="/sobre">Sobre</a>
          <a href="/servicos">Serviços</a>
          <a href="/conteudos">Conteúdos</a>
          <a href="/contato">Contato</a>
        </Links> */}

        <CTA href="/contato">Quero começar agora</CTA>

        <Legal>
          © {year} Inteligência Financeira S.A. • Todos os direitos reservados.
        </Legal>
      </Inner>
    </Wrap>
  );
};
