import React, { useState, useEffect } from "react";
import { CheckCircle } from "lucide-react";
import {
  TestimonialsContainer,
  Container,
  TitleGroup,
  Title,
  CarouselContainer,
  CarouselWrapper,
  TestimonialCard,
  TestimonialImage,
  Avatar,
  ClientName,
  ClientTitle,
  Quote,
  Rating,
  Star,
  ImagePlaceholder,
  VerifiedBadge,
  ResultStats,
  StatNumber,
  StatLabel,
} from "./styles";
import foto from "../../assets/example.png";

function Testimonials() {
  const [isPaused, setIsPaused] = useState(false);

  const testimonials = [
    {
      name: "Ana Paula Silva",
      title: "Executiva de Marketing",
      avatar: "AS",
      quote:
        "Por anos deixei meu dinheiro parado na poupanÃ§a por medo. O Estoicismo Financeiro me ensinou a ter clareza nas decisÃµes.",
      rating: 5,
      result: { number: "R$ 850k", label: "patrimÃ´nio atual" },
      hasImage: true, // Alterado para true
      image: foto,
    },
    // Outros testimonials comentados...
  ];

  const duplicatedTestimonials = [
    ...testimonials,
    ...testimonials,
    ...testimonials,
  ];
  const totalCards = duplicatedTestimonials.length;
  const originalLength = testimonials.length;

  const renderStars = (rating: number) => {
    return Array.from({ length: 5 }, (_, index) => (
      <Star key={index} delay={index * 0.1}>
        {index < rating ? "â˜…" : "â˜†"}
      </Star>
    ));
  };

  return (
    <div className="container">
      <TestimonialsContainer>
        <Container>
          <TitleGroup>
            <Title>Mensagens reais de quem aplicou o mÃ©todo</Title>
          </TitleGroup>

          <CarouselContainer>
            <CarouselWrapper
              onMouseEnter={() => setIsPaused(true)}
              onMouseLeave={() => setIsPaused(false)}
            >
              {duplicatedTestimonials.map((testimonial, index) => (
                <TestimonialCard key={`${testimonial.name}-${index}`}>
                  <VerifiedBadge>
                    <CheckCircle size={16} />
                  </VerifiedBadge>

                  {testimonial.hasImage ? (
                    <TestimonialImage>
                      <img
                        src={testimonial.image} // Usando a imagem do testimonial
                        alt={`Depoimento ${testimonial.name}`}
                        style={{
                          width: "100%",
                          height: "100%",
                          objectFit: "cover",
                          borderRadius: "8px",
                        }}
                      />
                    </TestimonialImage>
                  ) : (
                    <ImagePlaceholder>
                      <div style={{ fontSize: "2rem", marginBottom: "0.5rem" }}>
                        ðŸ“±
                      </div>
                      <div>Mensagem Real</div>
                      <div style={{ fontSize: "0.9rem", opacity: 0.8 }}>
                        Depoimento Verificado
                      </div>
                    </ImagePlaceholder>
                  )}

                  {/* Descomentei as seÃ§Ãµes para mostrar o testimonial completo */}
                  <div
                    style={{
                      display: "flex",
                      alignItems: "center",
                      gap: "1rem",
                      marginBottom: "1rem",
                    }}
                  >
                    <Avatar>{testimonial.avatar}</Avatar>
                    <div>
                      <ClientName>{testimonial.name}</ClientName>
                      <ClientTitle>{testimonial.title}</ClientTitle>
                    </div>
                  </div>

                  <Quote>{testimonial.quote}</Quote>
                  <Rating>{renderStars(testimonial.rating)}</Rating>

                  <ResultStats>
                    <StatNumber>{testimonial.result.number}</StatNumber>
                    <StatLabel>{testimonial.result.label}</StatLabel>
                  </ResultStats>
                </TestimonialCard>
              ))}
            </CarouselWrapper>
          </CarouselContainer>
        </Container>
      </TestimonialsContainer>
    </div>
  );
}

export default Testimonials;
