import styled from "styled-components";

const Wrap = styled.div`
  display: grid;
  gap: 18px;
`;

const Q = styled.div`
  background: var(--card);
  border: 1px solid rgba(212,175,55,.18);
  border-radius: 16px;
  padding: 18px;
`;

const H2 = styled.h2`
  color: var(--gold-strong);
  text-align: center;
  margin-bottom: 18px;
`;
