import styled, { keyframes } from "styled-components";

const float = keyframes`
  from { 
    transform: translateY(0px) 
  }
  50% { 
    transform: translateY(-10px) 
  }
  to { 
    transform: translateY(0px) 
  }
`;

// Animação de fade in up
const fadeInUp = keyframes`
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

export const Container = styled.div`
  text-align: center;
  margin-bottom: 3rem;
  animation: ${fadeInUp} 0.8s ease-out forwards;
  opacity: 0;
`;

export const TitleGroup = styled.div`
  display: inline-flex;
  align-items: center;
  gap: 0.75rem;
  margin-bottom: 2rem;
  animation: ${float} 6s ease-in-out infinite;
`;

export const Title = styled.h2`
  font-size: clamp(2rem, 4vw, 3rem);
  font-weight: 700;
  background: linear-gradient(45deg, #fbbf24, #f59e0b, #fcd34d);
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-size: 200% 200%;
  color: var(--gold-strong);
`;

export const Banner = styled.div`
  border-radius: 18px;
  padding: clamp(24px, 4vw, 40px);
  border: 1px solid rgba(212, 175, 55, 0.35);
  background: linear-gradient(
    180deg,
    rgba(30, 50, 42, 0.8),
    rgba(20, 32, 27, 0.9)
  );
  text-align: center;
  position: relative;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
  backdrop-filter: blur(10px);

  &::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 3px;
    background: linear-gradient(90deg, #fbbf24, #f59e0b, #fcd34d);
    border-radius: 18px 18px 0 0;
  }
`;

export const BonusText = styled.p`
  color: var(--subtext);
  font-size: clamp(1.1rem, 2.5vw, 1.3rem);
  margin-bottom: 1.5rem;
  line-height: 1.6;
`;

export const CallText = styled.div`
  background: rgba(251, 191, 36, 0.1);
  padding: 1.5rem;
  border-radius: 12px;
  border: 1px solid rgba(251, 191, 36, 0.3);
  margin-top: 1.5rem;
`;

export const CallTitle = styled.h3`
  color: #fbbf24;
  font-size: clamp(1.2rem, 3vw, 1.5rem);
  font-weight: 700;
  margin-bottom: 0.5rem;
`;

export const CallDescription = styled.p`
  color: var(--subtext);
  font-size: clamp(0.9rem, 2vw, 1.1rem);
  line-height: 1.6;
`;
