import styled from "styled-components";

export const Wrap = styled.div`
  display: grid;
  gap: 18px;
`;

export const Q = styled.div`
  background: var(--card);
  border: 1px solid rgba(212, 175, 55, 0.18);
  border-radius: 16px;
  padding: 18px;
`;

export const H2 = styled.h2`
  color: var(--gold-strong);
  text-align: center;
  margin-bottom: 18px;
`;
